import Vue from "vue";
import Vuex from "vuex";



Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    products: [],
    reviews: [],
    cart: ["Desert Flower, 16", "Mesquite, 16"],
    user: {}
  },
  mutations: {
    // used to change state sync
    set_products(state, products){
      state.products = products
    }
  },
  actions: {
    // Create async to pull from api
    fetchProductsData: (context) => {
      this.$http.get('http://localhost:8000/products').then(response => {
          context.commit('set_products', response.body)
      }).catch(error => console.log(error))
    }
  },
  getters: {
    getProducts(state){
      return state.products
    },
    getCart(state){
      return state.cart
    }
  }
});
