import axios from "axios";
export default {
  data: function data() {
    return {
      email: '',
      password: ''
    };
  },
  methods: {
    submit: function submit() {
      var _this = this;

      axios.post('http://localhost:8000/login', {
        email: this.email,
        password: this.password
      }).then(function (res) {
        _this.$store.state.user = {
          'token': res.data.token,
          'user_id': res.data.user.id,
          'firstName': res.data.user.firstName,
          "admin": res.data.user.admin
        }, _this.$store.state.isLoggedIn = true, _this.$router.push('/');
      }).catch(function (err) {
        console.error(err);
        alert('Error logging in please try again');
      });
      axios.get("http://localhost:8000/purchases/".concat(res.data.user.id)).then(function (res) {
        _this.$store.state.purchases = res;
      }).catch(function (err) {
        console.log(err);
        alert('Error getting purchases');
      });
    },
    clear: function clear() {
      this.email = '', this.password = '';
    }
  }
};
