import Vue from 'vue';
import VeeValidate from 'vee-validate';
Vue.use(VeeValidate);
export default {
  $_veeValidate: {
    validator: 'new'
  },
  data: function data() {
    return {
      email: '',
      password: '',
      dictionary: {
        attributes: {
          email: 'E-mail Address',
          password: 'Password' // custom attributes

        }
      }
    };
  },
  mounted: function mounted() {
    this.$validator.localize('en', this.dictionary);
  },
  methods: {
    submit: function submit() {
      this.$validator.validateAll();
    },
    clear: function clear() {
      this.email = '', this.password = '', this.$validator.reset();
    }
  }
};
